INSERT INTO Feedback (user_id, dish_id, rating, order_id, comment)
VALUES 
    (1, 1, 4, 1, 'Món Bò tơ cuốn bánh tráng có hương vị rất đặc biệt, thịt bò mềm và rau sống tươi. Tuy nhiên, phần ăn giao đến không được gói kỹ nên hơi nguội.'),
    (2, 1, 5, 2, 'Bò tơ cuốn bánh tráng là món ăn hoàn hảo để chia sẻ với gia đình. Hương vị tuyệt vời, phần rau rất sạch và tươi. Sẽ đặt thêm lần nữa.'),
    (3, 1, 3, 3, 'Phần bò tơ có hương vị ổn, nhưng bánh tráng giao đến bị khô. Hy vọng lần tới nhà hàng cải thiện cách đóng gói.'),
    (4, 2, 4, 4, 'Món Tan Tan Ramen ngon và nước dùng đậm đà. Tuy nhiên, mì hơi mềm hơn so với mong đợi vì thời gian giao hàng khá lâu.'),
    (5, 2, 5, 5, 'Tan Tan Ramen đúng chuẩn Nhật Bản. Gia vị vừa miệng, cay nhẹ rất ngon. Phần ăn giao đến vẫn còn nóng, rất đáng giá.'),
    (6, 2, 4, 6, 'Ramen có hương vị ngon, nhưng trứng chần trong ramen bị vỡ khi giao hàng. Hy vọng nhà hàng cải thiện việc đóng gói.'),
    (7, 3, 5, 7, 'Toku TanTan Ramen thực sự ngon, nước dùng đậm vị và phần topping đầy đủ. Tôi rất hài lòng với đơn hàng này.'),
    (8, 3, 4, 8, 'Món ramen cao cấp có hương vị rất ngon, nhưng giá hơi cao so với phần ăn. Sợi mì cũng mềm đi trong quá trình vận chuyển.'),
    (9, 3, 3, 9, 'Hương vị Toku TanTan Ramen không thực sự đặc biệt so với kỳ vọng của tôi. Hy vọng món ăn sẽ được nâng cấp về chất lượng.'),
    (10, 4, 3, 10, 'Miso Ramen có vị khá ngon, nhưng nước dùng bị nguội khi đến nơi. Nếu giao hàng nhanh hơn, trải nghiệm sẽ tốt hơn nhiều.'),
    (1, 4, 4, 11, 'Miso Ramen rất ngon, nước dùng đậm đà và phần ăn đầy đặn. Dù hơi nguội nhưng hương vị vẫn rất ổn.'),
    (2, 4, 5, 12, 'Miso Ramen ngon tuyệt, nước dùng đậm vị, topping phong phú. Đóng gói kỹ lưỡng giúp giữ nhiệt tốt.'),
    (3, 5, 4, 13, 'Yakisoba rất ngon, hương vị hấp dẫn. Tuy nhiên, phần rau củ hơi bị mềm do thời gian vận chuyển.'),
    (4, 5, 5, 14, 'Món Yakisoba đậm vị, sợi mì thơm ngon, phần thịt đầy đặn. Tôi rất hài lòng khi nhận được phần ăn còn nóng hổi.'),
    (5, 5, 4, 15, 'Hương vị Yakisoba ổn, phù hợp giá tiền. Nhưng phần ăn giao đến hơi nguội. Hy vọng món ăn được giữ nhiệt tốt hơn.'),
    (6, 6, 4, 16, 'Sashimi cá hồi rất tươi và ngon. Tuy nhiên, nước tương bị đổ ra hộp khi giao đến. Mong nhà hàng đóng gói cẩn thận hơn.'),
    (7, 6, 5, 17, 'Phần sashimi cá hồi đẹp mắt, cá tươi và phần wasabi chuẩn vị. Đây là một món ăn rất đáng để thử.'),
    (8, 6, 4, 18, 'Sashimi ngon và tươi, nhưng phần trình bày bị ảnh hưởng do vận chuyển. Nếu cải thiện khâu này sẽ rất hoàn hảo.'),
    (9, 7, 4, 19, 'Cơm cuộn phủ bơ có hương vị ngon, nhưng lớp bơ trên cơm bị dập khi giao đến. Hy vọng nhà hàng cải thiện bao bì.'),
    (10, 7, 5, 20, 'Cơm cuộn phủ bơ thực sự ngon và hấp dẫn, cơm dẻo và bơ thơm. Một lựa chọn hoàn hảo cho bữa ăn gia đình.'),
    (1, 7, 4, 21, 'Hương vị rất ngon, nhưng phần bơ bị chảy nhẹ trong quá trình vận chuyển. Tôi hy vọng lần tới món ăn sẽ được giữ lạnh tốt hơn.'),
    (2, 8, 4, 22, 'Hương vị tinh tế và độc đáo, món ăn này thực sự phù hợp để đặt về nhà. Tôi rất ấn tượng với chất lượng giao hàng.'),
    (3, 8, 4, 23, 'Món ăn được chế biến rất cẩn thận, khi nhận hàng vẫn còn nóng hổi. Tôi rất hài lòng với trải nghiệm đặt hàng lần này.'),
    (4, 8, 5, 24, 'Hương vị món ăn tuyệt vời, giao hàng nhanh chóng và đúng giờ. Chắc chắn tôi sẽ đặt lại món này.'),
    (5, 9, 4, 25, 'Món ăn ngon nhưng phần ăn hơi ít. Nếu khẩu phần lớn hơn sẽ phù hợp hơn với việc đặt giao về nhà.'),
    (6, 9, 4, 26, 'Hương vị món ăn rất tuyệt vời, từng nguyên liệu được chuẩn bị kỹ lưỡng. Món này rất thích hợp cho bữa tối tại nhà.'),
    (7, 9, 4, 27, 'Món ăn ngon và hấp dẫn nhưng giá cả hơi cao. Tuy nhiên, chất lượng món ăn giao về vẫn được đảm bảo tốt.'),
    (8, 10, 5, 28, 'Món ăn rất ngon, phù hợp với khẩu vị của tôi. Tôi rất hài lòng với cách đóng gói khi giao hàng.'),
    (9, 10, 4, 29, 'Hương vị món ăn ổn định, đóng gói kỹ lưỡng và giao hàng đúng giờ. Tôi rất hài lòng với dịch vụ.'),
    (10, 10, 4, 30, 'Món ăn không chỉ ngon mà còn có mức giá hợp lý. Giao hàng nhanh chóng, tôi sẽ tiếp tục ủng hộ.'),
    (1, 11, 4, 31, 'Món ăn ngon nhưng cần cải thiện thêm về khâu đóng gói để giữ được nhiệt độ tốt hơn khi giao.'),
    (2, 11, 5, 32, 'Món ăn rất ngon, trình bày đẹp mắt ngay cả khi được giao về nhà. Tôi cảm nhận được sự chăm chút từ nhà hàng.'),
    (3, 11, 4, 33, 'Hương vị món ăn rất tốt, mức giá hợp lý. Đóng gói sạch sẽ, tôi sẽ đặt thêm các món khác từ nhà hàng.'),
    (4, 12, 5, 34, 'Món ăn tuyệt vời, đóng gói kỹ càng và giữ được hương vị tươi ngon khi giao hàng. Tôi sẽ giới thiệu cho bạn bè.'),
    (5, 12, 4, 35, 'Món ăn ngon nhưng phần ăn hơi ít. Hy vọng khẩu phần sẽ lớn hơn trong các lần đặt hàng sau.'),
    (6, 12, 5, 36, 'Hương vị món ăn rất tuyệt vời, giao hàng đúng giờ và giữ được độ nóng. Tôi rất ấn tượng.'),
    (7, 13, 4, 37, 'Món ăn ngon nhưng giá có phần hơi cao. Giao hàng nhanh chóng và chất lượng món vẫn được đảm bảo.'),
    (8, 13, 4, 38, 'Món ăn rất ngon, giao hàng cẩn thận. Tôi sẽ thử thêm nhiều món khác từ nhà hàng.'),
    (9, 13, 4, 39, 'Hương vị món ăn ổn định, đóng gói cẩn thận. Tôi cảm thấy hài lòng với dịch vụ giao hàng của nhà hàng.'),
    (10, 14, 5, 40, 'Món ăn có hương vị thơm ngon, đóng gói cẩn thận. Giao hàng nhanh và giá cả hợp lý với chất lượng món ăn.'),
    (1, 14, 4, 41, 'Món ăn ngon nhưng khâu đóng gói cần được cải thiện hơn nữa để giữ nhiệt tốt hơn khi giao.'),
    (2, 14, 5, 42, 'Món ăn không chỉ ngon mà còn được trình bày đẹp mắt. Giao hàng đúng giờ, tôi rất hài lòng.'),
    (3, 15, 4, 43, 'Hương vị món ăn rất tốt, mức giá phù hợp với chất lượng. Đóng gói đẹp và giao hàng đúng hẹn. Tôi rất hài lòng.'),
    (4, 15, 5, 44, 'Món ăn tuyệt vời từ hương vị đến cách trình bày. Tôi rất hài lòng và sẽ giới thiệu cho bạn bè.'),
    (5, 15, 4, 45, 'Món ăn ngon nhưng phần ăn hơi ít so với mong đợi. Hy vọng sẽ có sự điều chỉnh về khẩu phần.'),
    (6, 16, 4, 46, 'Hương vị của món ăn thực sự rất tuyệt vời, xứng đáng để thử và tôi chắc chắn sẽ quay lại.'),
    (7, 16, 4, 47, 'Món ăn ngon nhưng giá cả hơi cao. Tuy nhiên, tôi vẫn đánh giá cao chất lượng món ăn.'),
    (8, 16, 5, 48, 'Món ăn rất ngon, hương vị độc đáo và đáng để thử lại nhiều lần. Tôi rất hài lòng với trải nghiệm này.'),
    (9, 17, 4, 49, 'Hương vị món ăn rất ổn định, phục vụ nhiệt tình và chu đáo. Tôi cảm thấy rất thoải mái khi dùng bữa.'),
    (10, 17, 4, 50, 'Món ăn có hương vị thơm ngon và giá cả hợp lý. Tôi sẽ tiếp tục ủng hộ nhà hàng trong tương lai.'),
    (1, 18, 4, 51, 'Món ăn rất ngon nhưng khâu phục vụ cần được cải thiện thêm để khách hàng có trải nghiệm tốt hơn.'),
    (2, 18, 5, 52, 'Món ăn ngon và được trình bày rất đẹp mắt. Tôi rất hài lòng và sẽ quay lại để thưởng thức thêm.'),
    (3, 18, 4, 53, 'Hương vị của món ăn rất tốt, mức giá hoàn toàn phù hợp với chất lượng. Tôi rất hài lòng.'),
    (4, 19, 5, 54, 'Món ăn này thực sự tuyệt vời, từ hương vị đến cách trình bày đều rất chỉnh chu. Tôi rất hài lòng.'),
    (5, 19, 4, 55, 'Món ăn rất ngon nhưng khẩu phần hơi ít. Hy vọng lần tới khi đặt hàng, khẩu phần sẽ được điều chỉnh nhiều hơn.'),
    (6, 19, 5, 56, 'Hương vị rất tuyệt vời, giao hàng nhanh chóng và món ăn vẫn còn nóng. Tôi rất hài lòng với trải nghiệm này.'),
    (7, 20, 4, 57, 'Món ăn ngon nhưng giá cả hơi cao so với kỳ vọng. Tuy nhiên, giao hàng đúng giờ và chất lượng ổn định.'),
    (8, 20, 5, 58, 'Món ăn được giao tới rất nhanh, hợp khẩu vị và vẫn giữ được độ nóng. Tôi sẽ đặt hàng thêm lần nữa.'),
    (9, 20, 4, 59, 'Hương vị món ăn ổn định, phục vụ giao hàng rất nhiệt tình. Tôi cảm thấy hài lòng và sẽ tiếp tục ủng hộ.'),
    (10, 21, 5, 60, 'Món ăn rất ngon với mức giá hợp lý. Mặc dù đặt hàng qua ứng dụng nhưng mọi thứ đều rất tuyệt.'),
    (1, 21, 4, 61, 'Món ăn ngon nhưng cần cải thiện thêm phần đóng gói để món ăn không bị nguội khi giao đến khách hàng.'),
    (2, 21, 5, 62, 'Món ăn ngon và đóng gói rất cẩn thận. Tôi chắc chắn sẽ đặt hàng thêm và giới thiệu cho bạn bè.'),
    (3, 22, 4, 63, 'Hương vị món ăn rất ngon, giao hàng đúng giờ và giá cả hợp lý. Một trải nghiệm đặt hàng tuyệt vời.'),
    (4, 22, 5, 64, 'Món ăn tuyệt vời cả về hương vị lẫn cách đóng gói. Tôi thực sự hài lòng và sẽ tiếp tục đặt hàng.'),
    (5, 22, 3, 65, 'Món ăn ngon nhưng khẩu phần hơi ít. Hy vọng nhà hàng cải thiện để phù hợp hơn với nhu cầu khách đặt hàng.'),
    (6, 23, 5, 66, 'Hương vị rất tuyệt vời và món ăn được giao nhanh chóng. Tôi chắc chắn sẽ đặt hàng thêm lần nữa.'),
    (7, 23, 4, 67, 'Món ăn ngon nhưng giá cả hơi cao. Giao hàng đúng giờ, tuy nhiên món ăn có phần hơi nguội.'),
    (8, 23, 5, 68, 'Món ăn rất ngon, giao hàng nhanh và đóng gói cẩn thận. Tôi sẽ tiếp tục đặt hàng tại đây.'),
    (9, 24, 4, 69, 'Món ăn giao đến vẫn giữ được độ nóng và hương vị ổn định. Tôi cảm thấy rất hài lòng.'),
    (10, 24, 5, 70, 'Món ăn ngon, giá hợp lý và đóng gói đẹp mắt. Tôi sẽ tiếp tục đặt hàng qua ứng dụng.'),
    (1, 25, 4, 71, 'Món ăn ngon nhưng cần cải thiện thêm về khâu đóng gói để món ăn giữ được độ nóng lâu hơn.'),
    (2, 25, 5, 72, 'Món ăn ngon, giao hàng đúng giờ và được đóng gói kỹ lưỡng. Một trải nghiệm đặt hàng rất tốt.'),
    (3, 25, 4, 73, 'Hương vị món ăn rất tốt và mức giá hợp lý. Tuy nhiên, món ăn hơi nguội khi được giao tới.'),
    (4, 26, 5, 74, 'Món ăn tuyệt vời cả về hương vị lẫn cách đóng gói. Tôi sẽ tiếp tục đặt hàng và giới thiệu cho bạn bè.'),
    (5, 26, 4, 75, 'Món bánh cuốn ăn rất ngon, nhưng phần ăn hơi ít. Hy vọng lần tới khẩu phần sẽ được tăng thêm để khách hàng hài lòng hơn.'),
    (6, 26, 5, 76, 'Hương vị bánh cuốn rất tuyệt vời, phần nhân được chế biến kỹ lưỡng và rau đi kèm rất tươi. Đóng gói cũng rất sạch sẽ.'),
    (7, 27, 4, 77, 'Món ăn ngon và đáng thử. Tuy nhiên, giá cả có phần hơi cao so với kỳ vọng. Hy vọng sẽ có ưu đãi cho khách hàng thân thiết.'),
    (8, 27, 5, 78, 'Bánh cuốn nhân trứng rất ngon, mềm và thơm. Cách đóng gói gọn gàng giúp món ăn giữ được độ nóng khi giao đến.'),
    (9, 27, 4, 79, 'Món ăn có hương vị ổn định và được giao hàng nhanh chóng. Tuy nhiên, phần nhân có thể nêm nếm đậm đà hơn.'),
    (10, 28, 5, 80, 'Món mì gà tần rất bổ dưỡng, nước dùng đậm đà và giao hàng đúng giờ. Chắc chắn tôi sẽ đặt thêm trong tương lai.'),
